import React from 'react'

export const PromotePawnOverlay = ({turn}) => {
    return (
        <div style={{
            width: '400px',
            height: '100px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            position: 'fixed',
            top: 0
        }}>
            <p style={{ fontSize: '3em' }}>{turn === 'white' ? '\u2657' : '\u265D'}</p>
            <p style={{ fontSize: '3em' }}>{turn === 'white' ? '\u2658' : '\u265E'}</p>
            <p style={{ fontSize: '3em' }}>{turn === 'white' ? '\u2656' : '\u265C'}</p>
            <p style={{ fontSize: '3em' }}>{turn === 'white' ? '\u2655' : '\u265B'}</p>
        </div>
    )
}
