import PawnFactory from "./Pieces/Pawn";
import RookFactory from "./Pieces/Rook";
import KnightFactory from "./Pieces/Knight";
import BishopFactory from "./Pieces/Bishop";
import QueenFactory from "./Pieces/Queen";
import KingFactory from "./Pieces/King";

const BoardFactory = () => {

    const initializeBoard = () => {
        return ([
            [RookFactory('black'), KnightFactory('black'), BishopFactory('black'), QueenFactory('black'), KingFactory('black'), BishopFactory('black'), KnightFactory('black'), RookFactory('black')],
            [PawnFactory('black'), PawnFactory('black'), PawnFactory('black'), PawnFactory('black'), PawnFactory('black'), PawnFactory('black'), PawnFactory('black'), PawnFactory('black')],
            [null, null, null, null, null, null, null, null],
            [null, null, null, null, null, null, null, null],
            [null, null, null, null, null, null, null, null],
            [null, null, null, null, null, null, null, null],
            [PawnFactory('white'), PawnFactory('white'), PawnFactory('white'), PawnFactory('white'), PawnFactory('white'), PawnFactory('white'), PawnFactory('white'), PawnFactory('white')],
            [RookFactory('white'), KnightFactory('white'), BishopFactory('white'), KingFactory('white'), QueenFactory('white'), BishopFactory('white'), KnightFactory('white'), RookFactory('white')]
        ])
    }

    const boardToString = () => {
        
        let boardString = '';

        if (this.board === undefined) return '';

        this.board.forEach(row => {
            boardString = boardString.concat('\n');
            row.forEach(square => {
                if (square === null) {
                    boardString = boardString.concat('null, ')
                } else {
                    boardString = boardString.concat(`${square.piece}`);
                }
            })
        });
        
        return boardString;        
    }

    return Object.create({
        board: initializeBoard(),
        boardToString: boardToString()
    })

}

export default BoardFactory;
