import React, { useState, useEffect } from 'react';
import Square from './Square';

const Board = ({ boardModel }) => {

    const [board, setBoard] = useState(boardModel);
    const [selectedSquare, setSelectedSquare] = useState('');

    const handleClick = (x, y ) => {
        setSelectedSquare([x, y]);
        console.log(x + ', ' + y)
        try {
            console.log(board[x][y].color + ' ' + board[x][y].piece)
        }
        catch {
            console.log('Empty')
        }
    }

    return (
        <div>
            {board.map((column, y) => {
                return (
                    <div style={{ display: 'flex' }}>
                        {column.map((square, x) => {
                            //row is 7 - y to make white on the bottom and black on top
                            //essentially iterates from top of board (black) down (white)
                            return <Square square={board[x][7 - y]} position={[x, y]} onClick={() => handleClick(x, 7 - y)} 
                            style={((selectedSquare[0] === x) && (selectedSquare[1] === 7 - y)) ? {backgroundColor: 'yellow'} : ''}/>
                        })}
                        <br />
                    </div>
                )
            })}
        </div>
    )
}

export default Board;
