import React from 'react';

const Square = ({ square, position }) => {

    const getPieceSymbol = () => {
        if (square.color === 'white') {
            switch (square.piece) {
                case 'Pawn':
                    return '&#9817;';
                case 'Bishop':
                    return '&#9815;';
                case 'Knight':
                    return '&#9816;';
                case 'Rook':
                    return '&#9814;';
                case 'Queen':
                    return '&#9813;';
                case 'King':
                    return '&#9812;';
                default: 
                    break;
            }
        }
        else {
            switch (square.piece) {
                case 'Pawn':
                    return '&#9823;';
                case 'Bishop':
                    return '&#9822;';
                case 'Knight':
                    return '&#9821;';
                case 'Rook':
                    return '&#9820;';
                case 'Queen':
                    return '&#9819;';
                case 'King':
                    return '&#9818;';
                default: 
                    break;
            }

        }

        return '';
    }

    return (
        <div style={{ width: '100px', height: '100px', backgroundColor: ((position[0] + position[1]) % 2 === 0) ? 'white' : 'gray' }}>
            {square === null ? `${position} null` : getPieceSymbol()}
        </div>
    )
}

export default Square;
