const RookFactory = (color) => {

    //If direction is +1, generates moves up. If direction is -1, generates moves down
    //Returns moves, which can be concatanated to a larger list of moves
    const generateMovesVertically = (board, position, color, direction) => {
        let moves = [];
        let encounteredPiece = false;

        for (let y = position[1] + direction; y < 8 && y >= 0 && !encounteredPiece; y = y + direction) {
            //If the space is not empty
            if (board[position[0]][y] !== null) {
                encounteredPiece = true;
                //If the enocountered piece is not of the same color as this piece, add as a legal move
                if (board[position[0]][y].color !== color) {
                    moves.push([position, [position[0], y]]);
                }
            } //If the space is empty add as a legal move 
            else {
                moves.push([position, [position[0], y]]);
            }
        }

        return moves;
    }

    const generateMovesHorizontally = (board, position, color, direction) => {
        let moves = [];
        let encounteredPiece = false;

        for (let x = position[1] + direction; x < 8 && x >= 0 && !encounteredPiece; x = x + direction) {
            //If the space is not empty
            if (board[x][position[1]] !== null) {
                encounteredPiece = true;
                //If the enocountered piece is not of the same color as this piece, add as a legal move
                if (board[x][position[1]].color !== color) {
                    moves.push([position, [x, position[1]]]);
                }
            } //If the space is empty add as a legal move 
            else {
                moves.push([position, [x, position[1]]]);
            }
        }

        return moves;
    }

    return Object.create({
        color: color,
        piece: 'Rook',

        //Position a two value array starting from lower left quadrant
        //White's bottom row is 0, black's bottom row is 7
        getMoves(board, position) {
            let moves = [];

            //Moves up
            moves = moves.concat(generateMovesVertically(board, position, color, 1));
            //Moves down
            moves = moves.concat(generateMovesVertically(board, position, color, -1));
            //Moves left
            moves = moves.concat(generateMovesHorizontally(board, position, color, 1));
            //Moves right
            moves = moves.concat(generateMovesHorizontally(board, position, color, -1));

            console.log(`position: ${position}, moves: ${moves}`)
            console.log(moves)
            return moves;
        }
    })
}

export default RookFactory;
